import React, { useState, useContext } from 'react';
import { LabelText } from 'govuk-react';
import MyFooter from './MyFooter';
import MyHeader from './MyHeader';
import MyUpdateButton from '../MyUpdateButton';
import { Button } from 'govuk-react';
import { CurrentContext } from '../../App';
import jq from 'jquery';

function UpdateMedicalRecordsPage() {
    
    const [forename, setForename] = useState('');
    const [surname, setSurname] = useState('');
    const [personDOB, setPersonDOB] = useState('');
    const [genderCode, setGenderCode] = useState('');
    const [postcode, setPostcode] = useState('');

    
    


    function handleCallBack(myNhsNumber) {
        setNhsNumber(myNhsNumber);
    }


    function onChangeCapture(e) {
        setNhsNumber(e.target.value);
    }
    function handleChangeForename(e) {
        setForename(e.target.value);
    }

    function handleChangeSurname(e) {
        setSurname(e.target.value);
    }

    function handleChangePersonDOB(e) {
        setPersonDOB(e.target.value);
    }

    function handleChangeGenderCode(e) {
        setGenderCode(e.target.value);
    }

    function handleChangePostcode(e) {
        setPostcode(e.target.value);
    }

    return (
        <>
            <MyHeader />

            <LabelText>Forename:</LabelText>
            <input type="text" value={forename} onChange={handleChangeForename} />
            <br />
            <LabelText>Surname:</LabelText>
            <input type="text" value={surname} onChange={handleChangeSurname} />
            <br />
            <LabelText>Person DOB:</LabelText>
            <input type="text" value={personDOB} onChange={handleChangePersonDOB} />
            <br />
            <LabelText>Gender Code:</LabelText>
            <input type="text" value={genderCode} onChange={handleChangeGenderCode} />
            <br />
            <LabelText>Postcode:</LabelText>
            <input type="text" value={postcode} onChange={handleChangePostcode} />
            <br />
            <MyUpdateButton
                forename={forename}
                surname={surname}
                personDOB={personDOB}
                genderCode={genderCode}
                postcode={postcode}
            />
            <MyFooter />
        </>
    );
}

export default UpdateMedicalRecordsPage;
