import { Button } from 'govuk-react';
import jq from 'jquery';
import { CurrentContext } from '../App';

function MyBtn(props) {
    const [medicalRecord, setMedicalRecord] = useState(null);

    function fetchMedicalRecord() {
        var nhsNumberData = { "nhsNumber": props.nhsNumber }
        var url_medicalRecords = 'http://localhost:4000/getMedicalRecordsPdo.php';

        jq.ajax({
            type: "POST",
            url: url_medicalRecords,
            mode: "no-core",
            data: nhsNumberData,
            success(data) {
                console.log(data);
                if (data === "no patients") {
                    props.callback(-100);
                } else {
                    var json = jq.parseJSON(data);
                    setMedicalRecord(json[0]);
                    props.callback(json[0].nhsNumber);
                }
            }
        });
    }

    return (
        <>
            <Button id="submitButton" onClick={fetchMedicalRecord}> View Medical Record {props.index} </Button>
            {medicalRecord &&
                <fieldset>
                    <legend>Medical Record {props.index}</legend>
                    <p><strong>Forename:</strong> {medicalRecord.Forename}</p>
                    <p><strong>Surname:</strong> {medicalRecord.Surname}</p>
                    <p><strong>NHS Number:</strong> {medicalRecord.NHSNumber}</p>
                    <p><strong>Vaccine ID:</strong> {medicalRecord.VaccineID}</p>
                    <p><strong>Dose Number:</strong> {medicalRecord.DoseNo}</p>
                    <p><strong>Vaccination Date:</strong> {medicalRecord.VaccinationDate}</p>
                    <p><strong>Total Number of Doses:</strong> {medicalRecord.TotalSeriesOfDoses}</p>
                    <p><strong>Country of Vaccination:</strong> {medicalRecord.CountryOfVaccination}</p>
                    <p><strong>Booster:</strong> {medicalRecord.Booster ? 'Yes' : 'No'}</p>
                </fieldset>
            }
        </>
    );
}

export default MyBtn;
