import { Button } from 'govuk-react';
import { CurrentContext } from './ViewMedicalRecords'; 

import jq from 'jquery';

function MyBtn(props) {

    function callTax() {
        var carRegData = { "CarReg": CurrentContext.carReg }
        var url_cartaxinfo = 'http://localhost:4000/carRegInfo.php';

        function fetchMedicalRecords(props) {



            jq.ajax(
                {
                    type: "POST",
                    url: "http://localhost:4000/getMedicalRecordsPdo.php",
                    mode: "no-core",
                    data: medicalRecords,
                    success(data) {
                        console.log(data);
                        if (data === "no cars") {
                            props.callback(-100);
                        } else {
                            var json = jq.parseJSON(data);
                            props.callback(json[0].tax);
                        }
                    }
                }

            );
        }

        <fieldset key={index}>
            <legend>Medical Record {index + 1}</legend>
            <p><strong>Forename:</strong> {record.Forename}</p>
            <p><strong>Surname:</strong> {record.Surname}</p>
            <p><strong>NHS Number:</strong> {record.NHSNumber}</p>
            <p><strong>Vaccine ID:</strong> {record.VaccineID}</p>
            <p><strong>Dose Number:</strong> {record.DoseNo}</p>
            <p><strong>Vaccination Date:</strong> {record.VaccinationDate}</p>
            <p><strong>Total Number of Doses:</strong> {record.TotalSeriesOfDoses}</p>
            <p><strong>Country of Vaccination:</strong> {record.CountryOfVaccination}</p>
            <p><strong>Booster:</strong> {record.Booster ? 'Yes' : 'No'}</p>
        </fieldset>
                
            
        }

        return (
            <>
                <Button onClick={fetchMedicalRecords}> Submit </Button>

            </>
        );
    }

export default MyBtn;
