import { Button } from 'govuk-react';
import jq from 'jquery';
import { CurrentContext } from '../App';
import { useContext } from 'react';

function MyBtn(props) {
    
    const currentContext = useContext(CurrentContext);

    function fetchMedicalRecords(props) {
        var url_medicalRecords = 'http://localhost:4000/getMedicalRecordsPdo.php';

        jq.ajax(
            {
                type: "POST",
                url: url_medicalRecords,
                data: props.nhsNumber,
                success(data) {
                    console.log('Response data:', data);
                    if (data === "no patients") {
                        props.callback(-100);
                    } else {
                        var json = jq.parseJSON(data);
                        props.callback(json[0].nhsNumber);
                        currentContext.setMedicalRecord(json[0]);
                    }
                }
            }
        );
        
        {
            currentContext.medicalRecord && (
                <fieldset>
                    <legend>Medical Record </legend>
                    <p><strong>Forename:</strong> {currentContext.medicalRecord.forename} </p>
                    <p><strong>Surname:</strong> {currentContext.medicalRecord.surname} </p>
                    <p><strong>NHS Number:</strong> {currentContext.medicalRecord.nhsNumber} </p>
                    {/* Add other fields from the medical record */}
                </fieldset>
            )
        }
       
            
    }

    return (
        <>
            <Button id="submitButton" onClick={() => fetchMedicalRecords(props)}> ViewMedicalRecords </Button>
            
        </>
    );
 }

export default MyBtn;
