import { H2 } from 'govuk-react';
import React, { useState, useEffect } from 'react';
import { LabelText } from "govuk-react";
import MyFooter from './MyFooter';
import MyHeader from './MyHeader';
import MyBtn from '../MyBtn';
import { CurrentContext } from '../../App'; // Import CurrentContext from App.js

function ViewMedicalRecords() {

    const [nhsNumber, setNhsNumber] = useState(-100);

    function handleCallBack(myNhsNumber) {
        setNhsNumber(myNhsNumber);
        CurrentContext.nhsNumber = myNhsNumber;
    }

    function onChangeCapture(e) {
        CurrentContext.nhsNumber = e.target.value
    }

    return (
        <>
            <MyHeader />
            <H2>VIEW MEDICAL RECORDS</H2>

            <LabelText>Enter your NHS Number</LabelText>
            <input onChange={onChangeCapture} />

            <MyBtn callback={handleCallBack} />

            <MyFooter />
        </>
    );
}

export default ViewMedicalRecords;
