import { H2 } from 'govuk-react';
import React, { useState, useEffect } from 'react';
import { LabelText } from 'govuk-react';
import MyFooter from './MyFooter';
import MyHeader from './MyHeader';
import MyBtn from '../MyBtn';

export const CurrentContext = React.createContext(null);

function ViewMedicalRecords() {
    const [medicalRecords, setMedicalRecords] = useState([]);
    const [nhsNumber, setNhsNumber] = useState('');

    function handleCallBack(myNhsNumber) {
        setNhsNumber(myNhsNumber);
    }

    function onChangeCapture(e) {
        setNhsNumber(e.target.value);
    }

    useEffect(() => {
        if (nhsNumber) {
            fetchMedicalRecords();
        }
    }, [nhsNumber]);

    function fetchMedicalRecords() {
        fetch(`http://localhost:4000/getMedicalRecordsPdo.php?nhsNumber=${nhsNumber}`)
            .then(response => response.json())
            .then(data => setMedicalRecords(data))
            .catch(error => console.error(error));
    }

    return (
        <>
            <MyHeader />
            <H2>VIEW MEDICAL RECORDS</H2>

            <LabelText>Enter your NHS Number</LabelText>
            <input value={nhsNumber} onChange={onChangeCapture} />

            <MyBtn nhsNumber={nhsNumber} callback={handleCallBack} />

            {medicalRecords.length > 0 &&
                medicalRecords.map((record, index) => (
                    <fieldset key={index}>
                        <legend>Medical Record {index + 1}</legend>
                        <p><strong>Forename:</strong> {record.Forename}</p>
                        <p><strong>Surname:</strong> {record.Surname}</p>
                        <p><strong>NHS Number:</strong> {record.NHSNumber}</p>
                        <p><strong>Vaccine ID:</strong> {record.VaccineID}</p>
                        <p><strong>Dose Number:</strong> {record.DoseNo}</p>
                        <p><strong>Vaccination Date:</strong> {record.VaccinationDate}</p>
                        <p><strong>Total Number of Doses:</strong> {record.TotalSeriesOfDoses}</p>
                        <p><strong>Country of Vaccination:</strong> {record.CountryOfVaccination}</p>
                        <p><strong>Booster:</strong> {record.Booster ? 'Yes' : 'No'}</p>
                    </fieldset>
                ))
            }

            <MyFooter />
        </>
    );
}

export default ViewMedicalRecords;
