import { Button } from 'govuk-react';
import jq from 'jquery';
import { CurrentContext } from './Pages/viewMedicalRecords';

function MyBtn(props) {

    function fetchMedicalRecords(props) {
        var nhsNumberData = { "nhsNumber": CurrentContext.nhsNumber }
        var url_medicalRecords = 'http://localhost:4000/getMedicalRecordsPdo.php';

        

        jq.ajax(
            {
                type: "POST",
                url: url_medicalRecords,
                mode: "no-core",
                data: nhsNumberData,
                success(data) {
                    console.log(data);
                    if (data === "no patients") {
                        props.callback(-100);
                    } else {
                        var json = jq.parseJSON(data);
                        props.callback(json[0].nhsNumber);
                    }
                }
            }

        );
        

       
            
    }

        return (
            <>
                <Button id="submitButton" onClick={fetchMedicalRecords}> ViewMedicalRecords </Button>
                <fieldset>
                    <legend>Medical Record </legend>
                    <p><strong>Forename:</strong> </p>
                    <p><strong>Surname:</strong> </p>
                    <p><strong>NHS Number:</strong> {CurrentContext.nhsNumber} </p>
                    <p><strong>Vaccine ID:</strong> </p>
                    <p><strong>Dose Number:</strong> </p>
                    <p><strong>Vaccination Date:</strong> </p>
                    <p><strong>Total Number of Doses:</strong> </p>
                    <p><strong>Country of Vaccination:</strong> </p>
                    <p><strong>Booster:</strong></p>
                </fieldset>
            </>
        );
    }

export default MyBtn;
