import React, { useState } from 'react';
import { H2, Button, Link } from 'govuk-react';
import MyHeader from './MyHeader';
import MyFooter from './MyFooter';

function UpdateMedicalRecordsPage() {
    const [forename, setForename] = useState('');
    const [surname, setSurname] = useState('');
    const [nhsNumber, setNhsNumber] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('forename', forename);
        formData.append('surname', surname);
        formData.append('nhsNumber', nhsNumber);

        fetch('http://localhost:4000/updateMedicalRecords.php', {
            method: 'POST',
            body: formData
        })
            .then(response => response.json())
            .then(data => {
                console.log('Server response:', data);
                // Handle the response from the server here
            })
            .catch(error => {
                console.error('Error updating medical records:', error);
            });
    }

    return (
        <div>
            <MyHeader />
            <H2>UPDATE MEDICAL RECORDS</H2>

            <form onSubmit={handleSubmit}>
                <div className="updateMedicalRecordsForm">
                    <label htmlFor="forename">Forename:</label>
                    <input type="text" id="forename" name="forename" value={forename} onChange={event => setForename(event.target.value)} />
                </div>

                <div className="updateMedicalRecordsForm">
                    <label htmlFor="surname">Surname:</label>
                    <input type="text" id="surname" name="surname" value={surname} onChange={event => setSurname(event.target.value)} />
                </div>

                <div className="updateMedicalRecordsForm">
                    <label htmlFor="nhsNumber">NHS Number:</label>
                    <input type="text" id="nhsNumber" name="nhsNumber" value={nhsNumber} onChange={event => setNhsNumber(event.target.value)} />
                </div>

                <button type="submit">Update</button>
            </form>
            <MyFooter/>
        </div>
    );
}

export default UpdateMedicalRecordsPage;
