import React, { useState, useContext } from 'react';
import { LabelText } from 'govuk-react';
import MyFooter from './MyFooter';
import MyHeader from './MyHeader';
import MyBtn from '../MyBtn';
import { CurrentContext } from '../../App';
import jq from 'jquery';

function UpdateMedicalRecordsPage() {
    const currentContext = useContext(CurrentContext);
    const [forename, setForename] = useState(currentContext.medicalRecord.Forename);
    const [surname, setSurname] = useState(currentContext.medicalRecord.Surname);
    const [personDOB, setPersonDOB] = useState(currentContext.medicalRecord.PersonDOB);
    const [genderCode, setGenderCode] = useState(currentContext.medicalRecord.GenderCode);
    const [postcode, setPostcode] = useState(currentContext.medicalRecord.Postcode);

    function handleUpdate() {
        var url_updateMedicalRecords = 'http://localhost:4000/updateMedicalRecordsPdo.php';

        jq.ajax({
            type: "POST",
            url: url_updateMedicalRecords,
            data: {
                nhsNumber: currentContext.medicalRecord.NHSNumber,
                forename: forename,
                surname: surname,
                personDOB: personDOB,
                genderCode: genderCode,
                postcode: postcode
            },
            success: function (data, textStatus, xhr) {
                console.log("Raw server response:", xhr.responseText);
                currentContext.setMedicalRecord({
                    ...currentContext.medicalRecord,
                    Forename: forename,
                    Surname: surname,
                    PersonDOB: personDOB,
                    GenderCode: genderCode,
                    Postcode: postcode
                });

                alert('Medical Record updated successfully!');
            },
            error: function (xhr, textStatus, errorThrown) {
                console.error(xhr.responseText);
                alert('An error occurred while updating the medical record.');
            }
        });
    }

    return (
        <>
            <MyHeader>Update Medical Records</MyHeader>
            <div className="govuk-width-container">
                <div className="govuk-grid-row">
                    <div className="govuk-grid-column-two-thirds">
                        {currentContext.medicalRecord ? (

                            <fieldset>
                                <legend>Current Medical Record </legend>
                                <p><strong>Forename:</strong> {currentContext.medicalRecord.Forename} </p>
                                <p><strong>Surname:</strong> {currentContext.medicalRecord.Surname} </p>
                                <p><strong>NHS Number:</strong> {currentContext.medicalRecord.NHSNumber} </p>
                                <p><strong>PersonDOB:</strong> {currentContext.medicalRecord.PersonDOB} </p>
                                <p><strong>GenderCode:</strong> {currentContext.medicalRecord.GenderCode} </p>
                                <p><strong>Postcode:</strong> {currentContext.medicalRecord.Postcode} </p>

                            </fieldset>

                        ) : (
                            <></>
                        )}


                        <fieldset>
                            <legend>Update Medical Record </legend>
                            <LabelText>Forename</LabelText>
                            <input type="text" value={forename} onChange={(event) => setForename(event.target.value)} />
                            <br />
                            <LabelText>Surname</LabelText>
                            <input type="text" value={surname} onChange={(event) => setSurname(event.target.value)} />
                            <br />
                            <LabelText>PersonDOB</LabelText>
                            <input type="date" value={personDOB} onChange={(event) => setPersonDOB(event.target.value)} />

                            <br />
                            <LabelText>Gender Code:</LabelText>
                            <input type="text" value={genderCode} onChange={(event) => setGenderCode(event.target.value)} />
                            <br />
                            <LabelText>Postcode:</LabelText>
                            <input type="text" value={postcode} onChange={(event) => setPostcode(event.target.value)} />

                            <br />

                        </fieldset>

                        <button onClick={handleUpdate}>Update</button>

                        <MyFooter />

                    </div>
                </div>
            </div>

        </>
    );

}

export default UpdateMedicalRecordsPage;
