import { H2 } from 'govuk-react';
import React, { useState, useEffect } from 'react';
import MyFooter from './MyFooter';
import MyHeader from './MyHeader';

function ViewMedicalRecords() {
    /*const MedicalRecordsFieldset = () => {
        const [data, setData] = useState([]);
    }
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('/api/medical-records');
                const data = await response.json();
                setData(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);*/

    return (
        <>
            <MyHeader/>
            <H2>VIEW MEDICAL RECORDS</H2>
            <div>
                <div class="subHeadingText">
                    <h2>Your GP Health Records</h2>
                </div>
                <div class="bodyText">
                    <p>
                        Your record shows personal data, such as your details, allergies and medications.
                    </p>
                    <p>
                        Depending on what your GP surgery shares, you may also see:
                        <ul>
                            <li>forename</li>
                            <li>surname</li>
                            <li>NHS Number</li>
                            <li>VaccineID</li>
                            <li>Dose Number</li>
                            <li>VaccinationDate</li>
                            <li>Total Number of Doses</li>
                            <li>CountryofVaccination</li>
                            <li>booster</li>
                        </ul>
                    </p>
                </div>
                
                
                <div>
                    <fieldset>
                    <legend>Medical Record</legend>
                      <p>
                        <strong>Forename:</strong> 
                      </p>
                      <p>
                        <strong>Surname:</strong> 
                      </p>
                      <p>
                        <strong>NHS Number:</strong> 
                      </p>
                      <p>
                        <strong>Vaccine ID:</strong> 
                      </p>
                      <p>
                        <strong>Dose Number:</strong> 
                      </p>
                      <p>
                        <strong>Vaccination Date:</strong> 
                      </p>
                      <p>
                        <strong>Total Number of Doses:</strong> 
                      </p>
                      <p>
                        <strong>Country of Vaccination:</strong> 
                      </p>
                      <p>
                        <strong>Booster:</strong> 
                      </p>
                    </fieldset>
                </div>  
                
            

                <br /><br />
            </div>

            <MyFooter/>
        </>
    );
}
export default ViewMedicalRecords;
