import { H2 } from 'govuk-react';
import React, { useState } from 'react';
import { LabelText } from 'govuk-react';
import MyFooter from './MyFooter';
import MyHeader from './MyHeader';
import MyBtn from '../MyBtn';

export const CurrentContext = React.createContext(null);

function ViewMedicalRecords() {
    const [nhsNumber, setNhsNumber] = useState('');

    function handleCallBack(myNhsNumber) {
        setNhsNumber(myNhsNumber);
    }

    function onChangeCapture(e) {
        setNhsNumber(e.target.value);
    }

    function fetchMedicalRecords() {
        fetch('http://localhost:4000/getMedicalRecordsPdo.php', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ nhsNumber }),
        })
            .then((response) => response.json())
            .then((data) => {
                setMedicalRecords(data);
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    const [medicalRecords, setMedicalRecords] = useState(null);

    return (
        <>
            <MyHeader />
            <H2>VIEW MEDICAL RECORDS</H2>

            <LabelText>Enter your NHS Number</LabelText>
            <input value={nhsNumber} onChange={onChangeCapture} />

            {medicalRecords ? (
                medicalRecords.map((record, index) => (
                    <MyBtn key={index} index={index + 1} record={record} />
                ))
            ) : (
                <p>Loading...</p>
            )}

            <MyFooter />
        </>
    );
}

export default ViewMedicalRecords;
